import React from 'react';
import './App.css';

class GameCardComponent extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            homeTeam: "home",
            awayTeam: "away",
            homeScore: null,
            awayScore: null

        }
    }



    render(){
        return(
            <div className="card">
                <div className="card-body">
                    <h5 className="card-title">{this.state.awayTeam} @  {this.state.homeTeam}</h5>
                    <h6 className="card-subtitle mb-4">52 - 71</h6>
                    <h6 className="card-text">Leaders</h6>
                    <ul className="d">
                        <li>
                            <h6 className="card-text">PTS:   Bradley Beal -  17</h6>
                        </li>
                        <li>
                            <h6 className="card-text">PTS:   Bradley Beal -  17</h6>
                        </li>
                        <li>
                            <h6 className="card-text">PTS:   Bradley Beal -  17</h6>
                        </li>
                    </ul>
                    <a href="#" className="btn btn-primary">Boxscore</a>
                </div>
            </div>
        )
    }
}

export default GameCardComponent;
