import React from 'react';
import './App.css';

class GameCardComponent extends React.Component{
    constructor(props){
        super(props);

        this.state = {
            error: null,
            isLoaded: false,
            team: [],
            player: [],
            games: ['sumthin'],
            homeTeam: "home",
            awayTeam: "away",
            homeScore: null,
            awayScore: null,
            pointsLeader: "leader",
            reboundsLeader: "leader",
            assistsLeader: "leader",
            points: null,
            rebounds: null,
            assists: null
        }
    
    
        this.formattedDate = this.formattedDate.bind(this);
        this.componentDidMount = this.componentDidMount.bind(this);    
    }

    formattedDate() {
        const date = new Date();
        let month = (1 + date.getMonth()).toString();
        const day = date.getDate().toString();
        const year = date.getFullYear().toString();

        if(date.getMonth() < 10){
            month = '0' + month;
        }

        return year + '-' + month + '-' +  day;
    }



    componentDidMount(){
        console.log(this.formattedDate());
        fetch('https://www.balldontlie.io/api/v1/games?dates[]=' + this.formattedDate())
        .then(res => res.json())
        .then(
            (info) => {
            this.setState({
                games: info,
                isLoaded:true
            });

            console.log(info.data[0].id);

        },
        (error) => {
            this.setState({
                isLoaded: true,
                error
            })
        }
        )     
    }



    render(){
        //const {games} = this.state;
        return(
            <div className="card">
                <div className="card-body">
                    <h4 className="card-title">{this.state.games.data[2].id} @  {}</h4>
                    <h6 className="card-subtitle mb-4">52 - 71</h6>
                    <h6 className="card-text">Leaders</h6>
                    <ul className="card-stats">
                        <li className="card-stat">
                            <h6 className="card-text">PTS:   {this.state.pointsLeader} -  {this.state.points}<br/></h6>
                        </li>
                        <li className="card-stat">
                            <h6 className="card-text">REB:  {this.state.reboundsLeader} -  {this.state.rebounds}<br/></h6>
                        </li>
                        <li className="card-stat">
                            <h6 className="card-text">AST:   {this.state.assistsLeader} -  {this.state.assists}<br/></h6>
                        </li>
                    </ul>
                    <a href="#" className="btn btn-primary">Boxscore</a>
                </div>
            </div>
        )
    }
}

export default GameCardComponent;
