import React from 'react';
import './App.css';

class GameCardComponent extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            team: [],
            player: [],
            homeTeam: "home",
            awayTeam: "away",
            homeScore: null,
            awayScore: null,
            pointsLeader: "leader",
            reboundsLeader: "leader",
            assistsLeader: "leader",
            points: null,
            rebounds: null,
            assists: null
        }

        this.formattedDate = this.formattedDate.bind(this);
        
    }



    componentDidMount(){
        console.log("mounted component");
        fetch('https://www.balldontlie.io/api/v1/teams/16')
        .then(res => res.json())
        .then((data) => {
            this.setState({
                team: data ,
                homeTeam: data.name
            })
        })
        .catch(console.log)
        console.log(this.state.player);
        this.formattedDate();
    }

    formattedDate() {
        const date = new Date();
        const month = date.getMonth().toString();
        const day = date.getDay().toString();
        const year = date.getFullYear().toString();
        console.log(month + day + year);
    }


    render(){
        return(
            <div className="card">
                <div className="card-body">
                    <h4 className="card-title">{this.state.awayTeam} @  {this.state.homeTeam}</h4>
                    <h6 className="card-subtitle mb-4">52 - 71</h6>
                    <h6 className="card-text">Leaders</h6>
                    <ul className="card-stats">
                        <li className="card-stat">
                            <h6 className="card-text">PTS:   {this.state.pointsLeader} -  {this.state.points}<br/></h6>
                        </li>
                        <li className="card-stat">
                            <h6 className="card-text">REB:  {this.state.reboundsLeader} -  {this.state.rebounds}<br/></h6>
                        </li>
                        <li className="card-stat">
                            <h6 className="card-text">AST:   {this.state.assistsLeader} -  {this.state.assists}<br/></h6>
                        </li>
                    </ul>
                    <a href="#" className="btn btn-primary">Boxscore</a>
                </div>
            </div>
        )
    }
}

export default GameCardComponent;
